import { REGISTRAR_USUARIO } from "./types";

export default (state, action) => {
  switch (action.type) {
    case REGISTRAR_USUARIO:
      localStorage.setItem("user", JSON.stringify(action.payload));
      return {
        ...state,
        user: action.payload
      };

    default:
      return state;
  }
};
