import React, { useContext, useState, useEffect } from "react";

import {
  Grid,
  Header,
  Button,
  Card,
  Container,
  Segment
} from "semantic-ui-react";

import Product from "../components/Product";
import Pagination from "../components/Pagination";
import AddProductForm from "../components/AddProductForm";

import Context from "../config/context";

export default function Products() {
  const context = useContext(Context);
  const { products, getProducts } = context;

  const [currentPage, setCurrentPage] = useState(1);
  const [cardsPerPage, setCardsPerPage] = useState(3);

  useEffect(() => {
    getProducts();
  }, []);

  // Get current products
  const indexOfLastProduct = currentPage * cardsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - cardsPerPage;
  const currentProducts = products.slice(
    indexOfFirstProduct,
    indexOfLastProduct
  );

  const views = products ? (
    currentProducts.map(product => <Product product={product} />)
  ) : (
    <Header>Nothing here!</Header>
  );

  const paginate = pageNumber => setCurrentPage(pageNumber);

  const pagination =
    products.length > cardsPerPage ? (
      <Pagination
        cardsPerPage={cardsPerPage}
        totalCards={products.length}
        paginate={paginate}
      />
    ) : null;

  return (
    <Container>
      <Segment>
        <Grid>
          <Grid.Column floated="left">
            <Header as="h1">Recent Products</Header>
          </Grid.Column>
          <Grid.Column>
            <AddProductForm />
          </Grid.Column>
        </Grid>
      </Segment>
      <Container>
        <Card.Group fluid itemsPerRow="3">
          {views}
        </Card.Group>
      </Container>
      <br />
      <center>{pagination}</center>
    </Container>
  );
}
