import React, { useContext, useState } from "react";

import { Form, Modal, Button } from "semantic-ui-react";

import Context from "../config/context";

export default function AddCategoryForm() {
  const context = useContext(Context);
  const { addCategory } = context;

  const [data, setData] = useState();

  data.name = "";
  data.picture = "";

  const [nameData, setNameData] = useState("");
  const [pictureData, setPictureData] = useState("");

  const handleChange = (e, { name, value }) => setData({ [name]: value });
  const handleChange1 = (e, { value }) => setNameData({ value });
  const handleChange2 = (e, { value }) => setPictureData({ value });

  const handleSubmit = () => {
    console.log(JSON.stringify(data));

    const category = {
      name: nameData,
      picture: pictureData
    };

    addCategory(category);
  };

  return (
    <Modal trigger={<Button primary>Add new Category</Button>}>
      <Modal.Header>Add new Category</Modal.Header>
      <Modal.Content>
        <Form onSubmit={handleSubmit}>
          <Form.Input
            name="name"
            label="Name"
            placeholder="Category name"
            onChange={handleChange}
            value={data}
          />
          <Form.Input
            name="image"
            label="Image"
            placeholder="Image URL"
            onChange={handleChange}
            value={data}
          />
          <Button type="submit">Add</Button>
        </Form>
      </Modal.Content>
    </Modal>
  );
}
