import { REGISTRAR_USUARIO, GET_PRODUCTS } from "./values";

export default (state, action) => {
  switch (action.type) {
    case REGISTRAR_USUARIO:
      localStorage.setItem("user", JSON.stringify(action.payload));
      return {
        ...state,
        user: action.payload
      };
    case GET_PRODUCTS:
      return {
        ...state,
        products: action.payload
      };

    default:
      return state;
  }
};
