import React, { useContext, useState } from "react";

import { Form, Modal, Button, Header } from "semantic-ui-react";

import Context from "../config/context";

export default function AddCategoryForm() {
  const [state, setState] = setState("");

  const context = useContext(Context);
  const { addCategory } = context;

  const handleChange = (e, { name, value }) => setState({ [name]: value });

  const handleSubmit = () => {
    const { name, picture } = state;
    const category = {
      name: name,
      picture: picture
    };

    console.log(category);

    addCategory(JSON.stringify(category));
    setState({ submittedName: name, submittedEmail: picture });
  };

  return (
    <Modal trigger={<Button primary>Add new Category</Button>}>
      <Modal.Header>Add new Category</Modal.Header>
      <Modal.Content>
        <Form onSubmit={handleSubmit}>
          <Form.Input id="name" label="Name" placeholder="Category name" />
          <Form.Input id="image" label="Image" placeholder="Image URL" />
          <Button type="submit">Add</Button>
        </Form>
      </Modal.Content>
    </Modal>
  );
}
