import React, { useReducer } from "react";
import clienteAxios from "../config/axios";

// context
import userContext from "./userContext";

// reducer
import userReducer from "./userReducer";

// types
import { REGISTRAR_USUARIO, GET_PRODUCTS } from "./types";

const UserState = props => {
  const initialState = {
    user: null,
    products: []
  };
  // Dispatch para ejercutar las acciones
  const [state, dispatch] = useReducer(userReducer, initialState);

  // funciones
  const saveUser = async user => {
    const res = await clienteAxios.post("/users/add", user);

    dispatch({
      type: REGISTRAR_USUARIO,
      payload: res.data
    });
  };
  const getProducts = async () => {
    const res = await clienteAxios.get("/products/all");

    dispatch({
      type: GET_PRODUCTS,
      payload: res.data
    });
  };

  return (
    <userContext.Provider
      value={{
        user: state.user,
        products: state.products,
        saveUser,
        getProducts
      }}
    >
      {props.children}
    </userContext.Provider>
  );
};

export default UserState;
