import React, {} from "react";

import "semantic-ui-css/semantic.min.css";
import { Grid, Card, Header, Button, Form } from "semantic-ui-react";

import Context from "../../config/context";

export default function Signin() {
  const context = useContext(Context);
  const { addUser } = context;

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleChange1 = (e, { value }) => setEmail({ value });
  const handleChange2 = (e, { value }) => setPassword({ value });

  const handleSubmit = () => {
    const user = {
      email: email.value,
      password: password.value
    };

    addUser(user);
  };
  return (
    <Grid verticalAlign="middle" columns={3} centered>
      <Grid.Row>
        <Grid.Column>
          <Card fluid>
            <Card.Content>
              <Header textAlign="center">Â¡Welcome to eCommerce!</Header>
              <Form onSubmit={handleSubmit}>
                <Form.Field>
                  <label>Email</label>
                  <input
                    placeholder="yourmail@mail.com"
                    type="email"
                    onChange={handleChange1}
                    value={email.value}
                  />
                </Form.Field>
                <Form.Field>
                  <label>Password</label>
                  <input
                    placeholder="*****"
                    type="password"
                    onChange={handleChange2}
                    value={password.value}
                  />
                </Form.Field>
                <Button primary type="submit" fluid>
                  Sign in
                </Button>
              </Form>
            </Card.Content>
          </Card>
        </Grid.Column>
      </Grid.Row>
    </Grid>
  );
}
