import React, { useContext, useState, useEffect } from "react";

import { Form, Modal, Button, Header, Dropdown } from "semantic-ui-react";

import Context from "../config/context";

export default function AddProductForm() {
  const context = useContext(Context);
  const { categories, getCategories, addProduct } = context;

  useEffect(() => {
    getCategories();
  }, []);

  const listCategories = categories.map(category => ({
    key: category.id,
    text: category.name,
    value: category.id
  }));

  const [name, setName] = useState("");
  const [picture1, setPicture] = useState("");

  const handleChange1 = (e, { value }) => setName({ value });
  const handleChange2 = (e, { value }) => setPicture({ value });

  const handleSubmit = () => {
    // TODO:
    const pictures = [];

    const category = {
      name: name.value
    };

    addProduct(category);
  };

  return (
    <Modal trigger={<Button primary>Add new Product</Button>}>
      <Modal.Header>Add new Product</Modal.Header>
      <Modal.Content>
        <Form>
          <Form.Inpu name="name" label="Name" placeholder="Product name" />
          <Form.Input
            name="description"
            label="Description"
            placeholder="Product description (Optional)"
          />
          <Form.Field>
            <Header as="h5">Category</Header>
            <Dropdown
              name="category"
              placeholder="Category"
              fluid
              selection
              options={listCategories}
            />
          </Form.Field>
          <Form.Group widths="equal">
            <Form.Input name="price" label="Price" placeholder="Price (USD)" />
            <Form.Input
              name="weight"
              label="Weight"
              placeholder="Weigth (Kg)"
            />
          </Form.Group>
          <Form.Group widths="equal">
            <Form.Input
              name="picture1"
              label="Image 1"
              placeholder="Image 1 URL"
            />
            <Form.Input
              name="picture2"
              label="Image 2"
              placeholder="Image 2 URL"
            />
            <Form.Input
              name="picture3"
              label="Image 3"
              placeholder="Image 3 URL"
            />
          </Form.Group>
          <Button type="submit">Add</Button>
        </Form>
      </Modal.Content>
    </Modal>
  );
}
